mapper.select:
SELECT 

FROM tb_office_dflz a

WHERE a.id = #{id}

SELECT
FROM tb_office_dflz a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.activity_name LIKE '%'||#{activityName}||'%' '%'+#{activityName}+'%' concat('%',#{activityName},'%')
AND a.organizer LIKE '%'||#{organizer}||'%' '%'+#{organizer}+'%' concat('%',#{organizer},'%')
AND a.activity_time = #{activityTime}
AND a.lr_date BETWEEN #{beginLrDate} AND #{endLrDate}
AND (c.id = #{company.id} OR c.parent_ids LIKE '%,'||#{company.id}||',%') CONCAT('%,', #{company.id}, ',%'))
AND (o.id = #{office.id} OR o.parent_ids LIKE '%,'||#{office.id}||',%') CONCAT('%,', #{office.id}, ',%'))
${sqlMap.dsf}
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC

SELECT 

FROM tb_office_dflz a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE tb_office_dflz SET 
	del_flag = #{DEL_FLAG_DELETE}
